CREATE TABLE `proyectoweb`.`usuario` ( `idUsuario` INT NOT NULL AUTO_INCREMENT , `nombre` VARCHAR(50) NOT NULL , `primerApellido` VARCHAR(50) NOT NULL , `segundoApellido` VARCHAR(50) NULL , `nombreUsuario` VARCHAR(15) NOT NULL , `contrasenna` VARCHAR(100) NOT NULL , `correo` VARCHAR(50) NOT NULL , `codArea` INT NOT NULL , `numTelefono` INT NOT NULL , `rol` INT NOT NULL DEFAULT '1' , `activo` INT NOT NULL , PRIMARY KEY (`idUsuario`)) ENGINE = InnoDB;